mapper.select:
SELECT CASE WHEN COUNT(*) = 0 THEN 'false' ELSE 'true' END
FROM camp_review
WHERE U_num_fk = #{U_num_fk}
AND C_ID_fk = #{C_ID_fk};

mapper.delete:
delete from camp_review
where U_num_fk = #{U_num_fk}
AND C_ID_fk = #{C_ID_fk}

mapper.update:
update camp_review
set C_R_title = #{C_R_title}
, C_R_text = #{C_R_text}
, C_R_update = now()
, C_R_score = #{C_R_score}
where U_num_fk = #{U_num_fk}
and C_ID_fk = #{C_ID_fk}
